import CartContext from "./cart-context";
import { useReducer, usereducer } from "react";

const defaultCartState = {
  items: [],
  totalAmmount: 0,
};
const cartReducer = (state, action) => {

    if(action.type==="ADD"){
        const updatedItems = state.items.concat(action.item)
        const updatedTotalAmmount = state.totalAmmount + action.item.price * action.item.ammount
        return {
            items:updatedItems,
            totalAmmount : updatedTotalAmmount
        }
    }
  return defaultCartState;
};

const CartProvider = (props) => {
  const [cartState, dispatchCartAction] = useReducer(
    cartReducer,
    defaultCartState
  );
  const addItemToCartHandeler = (item) => {
    dispatchCartAction({ type: "ADD", item: item });
  };

  const removeItemToCartHandeler = (id) => {};

  const cartContext = {
    items: cartState.items,
    totalAmmount: cartState.totalAmmount,
    addItem: addItemToCartHandeler,
    removeItem: removeItemToCartHandeler,
  };

  return (
    <CartContext.Provider value={cartContext}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartProvider;

//props.children?
//props.children ka matlab yeh hai jaise ki hmne koi bhi component khi pe use kra toh
//uske jo chledren honge wo bhi render ho jayenge.
//children means that jb bhi hum us component ko khi use krenge or kuch b extra usme dalenge
//tohn wo khud render ho jaiga.

